mapper.select:
SELECT deleted
FROM beitragMitglied
WHERE mitgliedId = #{id}

mapper.update:
UPDATE beitragMitglied
SET deleted = TRUE
WHERE mitgliedId = #{id}

mapper.select:
SELECT *
FROM beitragMitglied


	mitgliedsnummer = #{mitgliedsnummer}


	AND vorname LIKE #{vorname}


	AND nachname LIKE #{nachname}

SELECT COUNT(*)
FROM beitragBuchung
WHERE mitgliedId = #{mitgliedId}
	AND halbjahr = #{halbjahr.halbjahr}
	AND jahr = #{halbjahr.jahr}
	AND typ = 'RECHNUNG_BUNDESEBENE'
	AND storniert = FALSE

mapper.delete:
DELETE FROM beitragBuchung
WHERE mitgliedId = #{mitgliedId}
	AND halbjahr = #{halbjahr.halbjahr}
	AND jahr = #{halbjahr.jahr}
	AND vorausberechnung = true

mapper.update:
UPDATE beitragBuchung
SET storniert = TRUE
WHERE mitgliedId = #{mitgliedId}
	AND halbjahr = #{halbjahr.halbjahr}
	AND jahr = #{halbjahr.jahr}
	AND vorausberechnung = true

mapper.select:
SELECT *
FROM beitragBuchung
WHERE mitgliedId = #{mitgliedId}
	AND halbjahr = #{halbjahr.halbjahr}
	AND jahr = #{halbjahr.jahr}
	AND storniert = false

SELECT CASE WHEN count(*) > 0 THEN 1 ELSE 0 END
FROM beitragBuchung
WHERE mitgliedId = #{mitgliedId}
	AND halbjahr = #{halbjahr.halbjahr}
	AND jahr = #{halbjahr.jahr}
	AND vorausberechnung = TRUE
	AND storniert = FALSE
	AND typ = 'RECHNUNG_BUNDESEBENE'

SELECT *
FROM beitragZeitraum
WHERE halbjahr = #{halbjahr}
	AND jahr = #{jahr}
