mapper.update:
UPDATE beitragSammelLastschrift
SET
	faelligkeit = #{faelligkeit},
	ausgefuehrt = #{ausgefuehrt},
	bezeichnung = #{bezeichnung}
WHERE sammelLastschriftId = #{sammelLastschriftId}

mapper.select:
SELECT sl.*, COUNT(DISTINCT l.lastschriftId) AS anzahlLastschriften, SUM(b.betrag) AS betrag FROM beitragSammelLastschrift sl JOIN beitragLastschrift l ON sl.sammelLastschriftId = l.sammelLastschriftId JOIN beitragLastschriftRechnung lr ON l.lastschriftId = lr.lastschriftId JOIN beitragRechnung r ON lr.rechnungId = r.rechnungId JOIN beitragRechnungPosten p ON r.rechnungId = p.RechnungId JOIN beitragBuchung b ON p.buchungId = b.buchungId
ausgefuehrt = #{ausgefuehrt}
GROUP BY sl.sammelLastschriftId
