mapper.update:
UPDATE beitragRechnung
SET
	status = #{status}
WHERE rechnungId = #{rechnungId}

mapper.select:
SELECT *
FROM beitragBuchung AS b
WHERE mitgliedId = #{mitgliedId}

SELECT CASE WHEN MAX(rechnungsNummer) IS NULL THEN 0 ELSE MAX(rechnungsNummer) END
FROM beitragRechnung
WHERE YEAR(datum) = #{jahr}

SELECT r.*, m.vorname, m.nachname, ma.mahnungen, ma.frist AS letzteFrist, SUM(b.betrag) AS betrag FROM beitragRechnung AS r JOIN beitragMitglied AS m ON r.mitgliedID = m.mitgliedID LEFT JOIN ( SELECT rechnungId, MAX(mahnungArt) AS mahnungen, MAX(frist) AS frist FROM beitragMahnung GROUP BY rechnungId ) AS ma ON r.rechnungId = ma.rechnungId JOIN beitragRechnungPosten p ON r.rechnungId = p.rechnungId JOIN beitragBuchung b ON p.buchungId = b.buchungId
YEAR(r.datum) = #{erstellungsjahr}
AND r.status = #{status}
AND ( (ma.frist IS NULL AND r.frist < CURDATE()) OR (ma.frist < CURDATE()) )
AND r.mitgliedId = #{mitgliedId}
GROUP BY r.rechnungId

SELECT *
FROM beitragMahnung m
WHERE rechnungId = #{rechnungId}
